import React, { Component } from "react";
import Seccion from "./Seccion";
import NewTareaForm from "./NewTareaForm";

export default class Tablero extends Component {
  state = {
    tareasToDo: [
      {
        id: 1,
        name: "Hacer el cuestionario de IOP",
        desc: "Idiota tenias que hacerlo antes"
      },
      {
        id: 2,
        name: "Terminar la serie de Tevez",
        desc: "no es tan importante"
      }
    ],
    tareasDoing: [
      {
        id: 3,
        name: "Haciendo el desafio",
        desc: "..."
      },
      {
        id: 4,
        name: "Trabajar",
        desc: "..."
      }
    ],
    tareasFinished: [
      {
        id: 5,
        name: "Ir al neurologo",
        desc: "soy una vieja de 80"
      },
      {
        id: 6,
        name: "Ir al orl",
        desc: "soy una vieja de 80 parte II"
      }
    ],
    form: {
      name: "",
      desc: ""
    },
    selectedTarea: null
  };

  handleChange = e => {
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    });
  };

  selectTarea = (selectedTarea, type) => {
    selectedTarea.type = type;
    this.setState({
      selectedTarea,
      form: {
        name: selectedTarea.name,
        desc: selectedTarea.desc
      }
    });
  };

  unSelectTarea = () => {
    this.setState({
      selectedTarea: null,
      form: {
        name: "",
        desc: ""
      }
    });
  };

  goToDoing = (tareas, tarea, type) => {
    this.eliminarTarea(tareas, tarea, type);
    this.state.tareasDoing.push(tarea);
    this.setState({
      tareasDoing: this.state.tareasDoing,
      tarea
    });
  };

  goToToDo = (tareas, tarea, type) => {
    this.eliminarTarea(tareas, tarea, type);
    this.state.tareasToDo.push(tarea);
    this.setState({
      tareasToDo: this.state.tareasToDo,
      tarea
    });
  };

  goToFinished = (tareas, tarea, type) => {
    this.eliminarTarea(tareas, tarea, type);
    this.state.tareasFinished.push(tarea);
    this.setState({
      tareasFinished: this.state.tareasFinished,
      tarea
    });
  };

  eliminarTarea = (tareas, tarea, type) => {
    let index = tareas.indexOf(tarea);
    tareas.splice(index, 1);
    switch (type) {
      case 1:
        this.setState({
          tareasToDo: tareas
        });
        break;
      case 2:
        this.setState({
          tareasDoing: tareas
        });
        break;
      case 3:
        this.setState({
          tareasFinished: tareas
        });
        break;
      default:
        return null;
    }
  };

  editTarea = () => {};

  handleSubmit = async e => {
    e.preventDefault();
    let newTarea = {
      name: this.state.form.name,
      desc: this.state.form.desc
    };
    if (this.state.selectedTarea) {
      newTarea.id = this.state.selectedTarea.id;
    } else {
      newTarea.id =
        this.state.tareasDoing.length +
        this.state.tareasToDo.length +
        this.state.tareasFinished.length +
        1;
    }
    this.state.tareasToDo.push(newTarea);
  };

  render() {
    return (
      <div
        style={{
          "background-color": "#d1d1d1",
          padding: "20px 10px 20px 20px",
          "justify-content": "center"
        }}
      >
        {this.state.selectedTarea ? (
          <h4>Modificá la tarea:</h4>
        ) : (
          <h4>Agregá una nueva tarea:</h4>
        )}

        <NewTareaForm
          formValues={this.state.form}
          onChange={this.handleChange}
          onSubmit={this.handleSubmit}
          selectedTarea={this.state.selectedTarea}
          unSelectTarea={this.unSelectTarea}
        />

        <div
          style={{
            display: "flex",
            "margin-top": "40px"
          }}
        >
          <div style={{ "margin-right": "50px" }}>
            <Seccion
              title="To do"
              tareas={this.state.tareasToDo}
              color="#5191fd"
              type={1}
              goToDoing={this.goToDoing}
              goToToDo={this.goToToDo}
              goToFinished={this.goToFinished}
              eliminarTarea={this.eliminarTarea}
              selectTarea={this.selectTarea}
            />
          </div>
          <div style={{ "margin-right": "50px" }}>
            <Seccion
              title="Doing"
              tareas={this.state.tareasDoing}
              color="#e68a97"
              type={2}
              goToDoing={this.goToDoing}
              goToToDo={this.goToToDo}
              goToFinished={this.goToFinished}
              eliminarTarea={this.eliminarTarea}
              selectTarea={this.selectTarea}
            />
          </div>
          <div style={{ "margin-right": "50px" }}>
            <Seccion
              title="Finished"
              tareas={this.state.tareasFinished}
              color="#66a853"
              type={3}
              goToDoing={this.goToDoing}
              goToToDo={this.goToToDo}
              goToFinished={this.goToFinished}
              eliminarTarea={this.eliminarTarea}
              selectTarea={this.selectTarea}
            />
          </div>
        </div>
      </div>
    );
  }
}
