import React, { Component, useState } from "react";
import Seccion from "./Seccion";
import NewTareaForm from "./NewTareaForm";

const Tablero = props => {
  const [tareasToDo, setTareasToDo] = useState([
    {
      id: 1,
      name: "Hacer el cuestionario de IOP",
      desc: "Idiota tenias que hacerlo antes"
    },
    {
      id: 2,
      name: "Terminar la serie de Tevez",
      desc: "no es tan importante"
    }
  ]);

  const [tareasDoing, setTareasDoing] = useState([
    {
      id: 3,
      name: "Haciendo el desafio",
      desc: "..."
    },
    {
      id: 4,
      name: "Trabajar",
      desc: "..."
    }
  ]);

  const [tareasFinished, setTareasFinished] = useState([
    {
      id: 5,
      name: "Ir al neurologo",
      desc: "soy una vieja de 80"
    },
    {
      id: 6,
      name: "Ir al orl",
      desc: "soy una vieja de 80 parte II"
    }
  ]);

  const [form, setForm] = useState({ name: "", desc: "" });

  const [selectedTarea, setSelectedTarea] = useState(null);

  const handleChange = e => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    });
  };

  const selectTarea = (selectedTarea, type) => {
    selectedTarea.type = type;
    this.setState({
      selectedTarea,
      form: {
        name: selectedTarea.name,
        desc: selectedTarea.desc
      }
    });
  };

  const unSelectTarea = () => {
    this.setState({
      selectedTarea: null,
      form: {
        name: "",
        desc: ""
      }
    });
  };

  const goToDoing = (tareas, tarea, type) => {
    eliminarTarea(tareas, tarea, type);
    tareasDoing.push(tarea);
    this.setTareasDoing({
      tareasDoing,
      tarea
    });
  };

  const goToToDo = (tareas, tarea, type) => {
    eliminarTarea(tareas, tarea, type);
    tareasToDo.push(tarea);
    this.setTareasToDo(tareasToDo, tarea);
  };

  const goToFinished = (tareas, tarea, type) => {
    this.eliminarTarea(tareas, tarea, type);
    this.state.tareasFinished.push(tarea);
    this.setState({
      tareasFinished: this.state.tareasFinished,
      tarea
    });
  };

  const eliminarTarea = (tareas, tarea, type) => {
    let index = tareas.indexOf(tarea);
    tareas.splice(index, 1);
    switch (type) {
      case 1:
        this.setState({
          tareasToDo: tareas
        });
        break;
      case 2:
        this.setState({
          tareasDoing: tareas
        });
        break;
      case 3:
        this.setState({
          tareasFinished: tareas
        });
        break;
      default:
        return null;
    }
  };

  const editTarea = () => {};

  const handleSubmit = async e => {
    e.preventDefault();
    let newTarea = {
      name: this.state.form.name,
      desc: this.state.form.desc
    };
    if (this.state.selectedTarea) {
      newTarea.id = this.state.selectedTarea.id;
      switch (this.state.selectedTarea.type) {
        case 1:
          this.eliminarTarea(
            this.state.tareasToDo,
            this.state.selectedTarea,
            this.state.selectedTarea.type
          );
          this.state.tareasToDo.push(newTarea);
          break;
        case 2:
          this.eliminarTarea(
            this.state.tareasDoing,
            this.state.selectedTarea,
            this.state.selectedTarea.type
          );
          this.state.tareasDoing.push(newTarea);
          break;
        case 3:
          this.eliminarTarea(
            this.state.tareasFinished,
            this.state.selectedTarea,
            this.state.selectedTarea.type
          );
          this.state.tareasFinished.push(newTarea);
          break;
        default:
          break;
      }
    } else {
      newTarea.id =
        this.state.tareasDoing.length +
        this.state.tareasToDo.length +
        this.state.tareasFinished.length +
        1;
      this.state.tareasToDo.push(newTarea);
    }

    this.setState({
      tareasToDo: this.state.tareasToDo,
      form: {
        name: "",
        desc: ""
      },
      selectedTarea: null
    });
  };

  return (
    <div
      style={{
        "background-color": "#d1d1d1",
        padding: "20px 10px 20px 20px",
        "justify-content": "center"
      }}
    >
      {selectedTarea ? (
        <h4>Modificá la tarea:</h4>
      ) : (
        <h4>Agregá una nueva tarea:</h4>
      )}

      <NewTareaForm
        formValues={form}
        onChange={handleChange}
        onSubmit={handleSubmit}
        selectedTarea={selectedTarea}
        unSelectTarea={unSelectTarea}
      />

      <div
        style={{
          display: "flex",
          "margin-top": "40px"
        }}
      >
        <div style={{ "margin-right": "50px" }}>
          <Seccion
            title="To do"
            tareas={tareasToDo}
            color="#5191fd"
            type={1}
            goToDoing={goToDoing}
            goToToDo={goToToDo}
            goToFinished={goToFinished}
            eliminarTarea={eliminarTarea}
            selectTarea={selectTarea}
          />
        </div>
        <div style={{ "margin-right": "50px" }}>
          <Seccion
            title="Doing"
            tareas={tareasDoing}
            color="#e68a97"
            type={2}
            goToDoing={goToDoing}
            goToToDo={goToToDo}
            goToFinished={goToFinished}
            eliminarTarea={eliminarTarea}
            selectTarea={selectTarea}
          />
        </div>
        <div style={{ "margin-right": "50px" }}>
          <Seccion
            title="Finished"
            tareas={tareasFinished}
            color="#66a853"
            type={3}
            goToDoing={goToDoing}
            goToToDo={goToToDo}
            goToFinished={goToFinished}
            eliminarTarea={eliminarTarea}
            selectTarea={selectTarea}
          />
        </div>
      </div>
    </div>
  );
};

export default Tablero;

