import React, { Component } from "react";
import Seccion from "./Seccion";
import NewTareaForm from "./NewTareaForm";

export default class Tablero extends Component {
  state = {
    tareasToDo: [
      {
        id: 1,
        name: "Hacer el cuestionario de IOP",
        desc: "Idiota tenias que hacerlo antes"
      },
      {
        id: 2,
        name: "Terminar la serie de Tevez",
        desc: "no es tan importante"
      }
    ],
    tareasDoing: [
      {
        id: 3,
        name: "Haciendo el desafio",
        desc: "..."
      },
      {
        id: 4,
        name: "Trabajar",
        desc: "..."
      }
    ],
    tareasFinished: [
      {
        id: 5,
        name: "Ir al neurologo",
        desc: "soy una vieja de 80"
      },
      {
        id: 6,
        name: "Ir al orl",
        desc: "soy una vieja de 80 parte II"
      }
    ],
    form: {
      name: "",
      desc: ""
    },
    selectedTarea: null
  };

  handleChange = e => {
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    });
  };

  selectTarea = selectedTarea => {
    this.setState({
      selectedTarea
    });
  };

  toToDo = tarea => {
    this.state.tareasToDo.push(tarea);
    this.setState({
      tareasToDo: this.state.tareasToDo,
      tarea
    });
  };

  handleSubmit = async e => {
    e.preventDefault();
    let newTarea = {
      id:
        this.state.tareasDoing.length +
        this.state.tareasToDo.length +
        this.state.tareasFinished.length +
        1,
      name: this.state.form.name,
      desc: this.state.form.desc
    };
    this.state.tareasToDo.push(newTarea);
    this.setState({
      tareasToDo: this.state.tareasToDo,
      newTarea
    });
  };

  render() {
    return (
      <div
        style={{
          "background-color": "#d1d1d1",
          padding: "20px 10px 20px 20px",
          "justify-content": "center"
        }}
      >
        <h4>Agregá una nueva tarea:</h4>
        <NewTareaForm
          formValues={this.state.form}
          onChange={this.handleChange}
          onSubmit={this.handleSubmit}
        />

        <div
          style={{
            display: "flex",
            "margin-top": "40px"
          }}
        >
          <div style={{ "margin-right": "50px" }}>
            <Seccion
              title="To do"
              tareas={this.state.tareasToDo}
              color="#5191fd"
              type={1}
            />
          </div>
          <div style={{ "margin-right": "50px" }}>
            <Seccion
              title="Doing"
              tareas={this.state.tareasDoing}
              color="#e68a97"
              type={2}
              toDo={this.toToDo}
            />
          </div>
          <div style={{ "margin-right": "50px" }}>
            <Seccion
              title="Finished"
              tareas={this.state.tareasFinished}
              color="#66a853"
              type={3}
            />
          </div>
        </div>
      </div>
    );
  }
}
