import React, { Component } from "react";
import Seccion from "./Seccion";

export default class Tablero extends Component {
  state = {
    tareasToDo: [
      {
        id: 1,
        name: "Hacer el cuestionario de IOP",
        desc: "Idiota tenias que hacerlo antes"
      },
      {
        id: 2,
        name: "Terminar la serie de Tevez",
        desc: "no es tan importante"
      }
    ],
    tareasDoing: [
      {
        id: 3,
        name: "Haciendo el desafio",
        desc: "..."
      },
      {
        id: 4,
        name: "Trabajar",
        desc: "..."
      }
    ],
    tareasFinished: [
      {
        id: 5,
        name: "Ir al neurologo",
        desc: "soy una vieja de 80"
      },
      {
        id: 6,
        name: "Ir al orl",
        desc: "soy una vieja de 80 parte II"
      }
    ]
  };
  render() {
    return (
      <div
        style={{
          "background-color": "#d1d1d1",
          display: "flex",
          "justify-content": "center"
        }}
      >
        <div style={{ "margin-right": "50px" }}>
          <Seccion title="To do" tareas={this.state.tareasToDo} />
        </div>
        <div style={{ "margin-right": "50px" }}>
          <Seccion title="Doing" tareas={this.state.tareasDoing} />
        </div>
        <div style={{ "margin-right": "50px" }}>
          <Seccion title="Finished" tareas={this.state.tareasFinished} />
        </div>
      </div>
    );
  }
}
