import React, { Component } from "react";

export default class TareasList extends Component {
  render() {
    return (
      <div style={{ "justify-content": "center" }}>
        <ul style={{ "list-style-type": "none" }}>
          {this.props.tareas.map(tarea => {
            return (
              <li
                className="bg-white px-4 py-4 shadow mb-4 card border "
                style={{ cursor: "pointer" }}
                onClick={() => this.props.selectTarea(tarea, this.props.type)}
              >
                <div
                  className=""
                  style={{
                    display: "flex"
                  }}
                >
                  <h4>{tarea.id}.</h4>
                  <h4> {tarea.name} </h4>
                </div>
                <p>{tarea.desc} </p>
                {this.props.type === 2 || this.props.type === 3 ? (
                  <button
                    onClick={() =>
                      this.props.goToToDo(
                        this.props.tareas,
                        tarea,
                        this.props.type
                      )
                    }
                    className="btn btn-primary"
                  >
                    To Do
                  </button>
                ) : null}
                {this.props.type === 1 || this.props.type === 3 ? (
                  <button
                    onClick={() =>
                      this.props.goToDoing(
                        this.props.tareas,
                        tarea,
                        this.props.type
                      )
                    }
                    className="btn btn-danger"
                  >
                    Doing
                  </button>
                ) : null}

                {this.props.type === 1 || this.props.type === 2 ? (
                  <button
                    onClick={() =>
                      this.props.goToFinished(
                        this.props.tareas,
                        tarea,
                        this.props.type
                      )
                    }
                    className="btn btn-success"
                  >
                    Finished
                  </button>
                ) : null}

                <button
                  style={{ "margin-left": "15px" }}
                  className="btn btn-danger"
                  onClick={() => {
                    this.props.eliminarTarea(
                      this.props.tareas,
                      tarea,
                      this.props.type
                    );
                  }}
                >
                  Eliminar
                </button>
              </li>
            );
          })}
        </ul>
      </div>
    );
  }
}
